CREATE TABLE empresa (idEmpresa INTEGER NOT NULL AUTO_INCREMENT, nombre VARCHAR(255) NOT NULL,nit VARCHAR(45) NOT NULL,cantidadCuentas INTEGER NOT NULL,PRIMARY KEY(idEmpresa));
CREATE TABLE panelCambios (idPanelCambios INTEGER NOT NULL AUTO_INCREMENT,fecha DATE NOT NULL,motivo VARCHAR(255) NOT NULL,version INTEGER NOT NULL,PRIMARY KEY(idPanelCambios));
CREATE TABLE controlVersiones (idcontrolVersiones INTEGER NOT NULL AUTO_INCREMENT,version INTEGER NOT NULL,matrices INTEGER NOT NULL,PRIMARY KEY(idcontrolVersiones));
CREATE TABLE usuario (idUsuario INTEGER NOT NULL AUTO_INCREMENT,empresa_idEmpresa INTEGER NOT NULL,nombre VARCHAR(150) NOT NULL,apellido VARCHAR(150) NOT NULL,cargo VARCHAR(150) NOT NULL,permiso VARCHAR(150) NOT NULL,PRIMARY KEY(idUsuario),FOREIGN KEY(empresa_idEmpresa)REFERENCES empresa(idEmpresa));
CREATE TABLE administrador (idAdministrador INTEGER NOT NULL AUTO_INCREMENT,usuario_idUsuario INTEGER NOT NULL,correo VARCHAR(150) NOT NULL,contrasena VARCHAR(150) NOT NULL,PRIMARY KEY(idAdministrador),FOREIGN KEY(usuario_idUsuario)REFERENCES usuario(idUsuario));
CREATE TABLE matrizLegal (idmatrizLegal INTEGER NOT NULL AUTO_INCREMENT,administrador_idAdministrador INTEGER NOT NULL,tema VARCHAR(255) NOT NULL,tipoNorma VARCHAR(255) NOT NULL,normaAplicar VARCHAR(255) NOT NULL,fecha DATETIME NOT NULL,estado VARCHAR(1) NOT NULL,PRIMARY KEY(idmatrizLegal),FOREIGN KEY(administrador_idAdministrador)REFERENCES administrador(idAdministrador));
CREATE TABLE matrizLegalEmisor (idmatrizLegalEmisor INTEGER NOT NULL AUTO_INCREMENT,matrizLegal_idmatrizLegal INTEGER NOT NULL,anoPublicacion DATE NOT NULL,enteEmisor VARCHAR(255) NOT NULL,descripcion VARCHAR(255) NOT NULL,articulo VARCHAR(255) NOT NULL,fechaEmision DATE NOT NULL,PRIMARY KEY(idmatrizLegalEmisor),FOREIGN KEY(matrizLegal_idmatrizLegal)REFERENCES matrizLegal(idmatrizLegal));
CREATE TABLE usuConsulta (idUsuConsulta INTEGER NOT NULL AUTO_INCREMENT,usuario_idUsuario INTEGER NOT NULL,correo VARCHAR(150) NOT NULL,contrasena VARCHAR(150) NOT NULL,PRIMARY KEY(idUsuConsulta),FOREIGN KEY(usuario_idUsuario)REFERENCES usuario(idUsuario));
CREATE TABLE matrizLegalCalificacion (idmatrizLegalCalificacion INTEGER NOT NULL AUTO_INCREMENT,matrizLegal_idmatrizLegal INTEGER NOT NULL,cumplimiento VARCHAR(255) NOT NULL,evaluacionCumplimiento VARCHAR(255) NOT NULL,controlesCumplimiento VARCHAR(255) NOT NULL,evidenciaCumplimiento VARCHAR(255) NOT NULL,PRIMARY KEY(idmatrizLegalCalificacion),FOREIGN KEY(matrizLegal_idmatrizLegal)REFERENCES matrizLegal(idmatrizLegal));
CREATE TABLE matrizLegalComplemento (idmatrizLegalComplemento INTEGER NOT NULL AUTO_INCREMENT,matrizLegal_idmatrizLegal INTEGER NOT NULL,procesoAplicacion VARCHAR(255) NOT NULL,urlNorma VARCHAR(255) NOT NULL,anotaciones VARCHAR(255) NOT NULL,PRIMARY KEY(idmatrizLegalComplemento),FOREIGN KEY(matrizLegal_idmatrizLegal)REFERENCES matrizLegal(idmatrizLegal));